// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

import "./ENSC_ENERGY.sol";

contract CrowdFunding {
  struct Campaign {
    address owner;
    string title;
    string description;
    uint256 target;
    uint256 deadline;
    uint256 amountCollected;
    string image;
    address[] donators;
    uint256[] donations;
  }

  ENSC_ENERGY public tokenContract; // Declare a state variable to hold the token contract's instance

  mapping(uint256 => Campaign) public campaigns;
  uint256 public numberOfCampaigns = 0;

  constructor(address payable _tokenContractAddress) { // Change the type of _tokenContractAddress to address payable
    tokenContract = ENSC_ENERGY(_tokenContractAddress); // Initialize the token contract in the constructor
  }

  function createCampaign(
    address _owner,
    string memory _title,
    string memory _description,
    uint256 _target,
    uint256 _deadline,
    string memory _image,
    uint256 _enscAmount // Add a parameter for the ENSC token amount
  ) public returns (uint256) {
    Campaign storage campaign = campaigns[numberOfCampaigns];

    require(campaign.deadline < block.timestamp, "The deadline should be a date in the future.");

    campaign.owner = _owner;
    campaign.title = _title;
    campaign.description = _description;
    campaign.target = _target;
    campaign.deadline = _deadline;
    campaign.amountCollected = 0;
    campaign.image = _image;

    // Transfer ENSC tokens from the sender to the contract
    require(tokenContract.transferFrom(msg.sender, address(this), _enscAmount), "Token transfer failed");

    numberOfCampaigns++;

    return numberOfCampaigns - 1;
  }

  function donateToCampaign(uint256 _id, uint256 _enscAmount) public {
    Campaign storage campaign = campaigns[_id];

    campaign.donators.push(msg.sender);
    campaign.donations.push(_enscAmount);

    // Transfer ENSC tokens from the sender to the contract
    require(tokenContract.transferFrom(msg.sender, address(this), _enscAmount), "Token transfer failed");

    campaign.amountCollected = campaign.amountCollected + _enscAmount;
  }

  function getDonators(uint256 _id) view public returns (address[] memory, uint256[] memory) {
    return (campaigns[_id].donators, campaigns[_id].donations);
  }

  function getCampaigns() public view returns (Campaign[] memory) {
    Campaign[] memory allCampaigns = new Campaign[](numberOfCampaigns);

    for(uint i = 0; i < numberOfCampaigns; i++) {
      Campaign storage item = campaigns[i];

      allCampaigns[i] = Campaign({
        owner: item.owner,
        title: item.title,
        description: item.description,
        target: item.target,
        deadline: item.deadline,
        amountCollected: item.amountCollected,
        image: item.image,
        donators: item.donators,
        donations: item.donations
      });
    }

    return allCampaigns;
  }
}

